import React, { useState, useEffect, useRef } from "react";
import { Document, Page, pdfjs } from "react-pdf";
import HTMLFlipBook from "react-pageflip";
import { useLocation } from "react-router-dom";
import "react-pdf/dist/esm/Page/AnnotationLayer.css";
import "react-pdf/dist/esm/Page/TextLayer.css";

// Set the worker source for react-pdf
pdfjs.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

const Flipbook = () => {
  const location = useLocation();
  const pdfUrl = location.state?.pdfUrl || "";
  const [numPages, setNumPages] = useState(null);
  const flipbookRef = useRef(null);
  const [isFlipbookReady, setIsFlipbookReady] = useState(false); // Track readiness

  useEffect(() => {
    console.log("PDF URL received:", pdfUrl);
  }, [pdfUrl]);

  const onDocumentLoadSuccess = ({ numPages }) => {
    console.log("PDF Loaded, Total Pages:", numPages);
    setNumPages(numPages);
  };

  // Wait for flipbook to mount before setting ref
  useEffect(() => {
    if (flipbookRef.current) {
      setIsFlipbookReady(true);
      console.log("Flipbook is ready:", flipbookRef.current);
    }
  }, [flipbookRef.current]);

  const nextPage = () => {
    if (!isFlipbookReady || !flipbookRef.current) {
      console.warn("Flipbook is not ready yet!");
      return;
    }

    console.log("Flipbook instance:", flipbookRef.current);
    console.log("Available methods:", Object.keys(flipbookRef.current));

    if (typeof flipbookRef.current.flipNext === "function") {
      flipbookRef.current.flipNext();
    } else {
      console.warn("flipNext() is not available on flipbookRef.current!");
    }
  };

  const prevPage = () => {
    if (!isFlipbookReady || !flipbookRef.current) {
      console.warn("Flipbook is not ready yet!");
      return;
    }

    if (typeof flipbookRef.current.flipPrev === "function") {
      flipbookRef.current.flipPrev();
    } else {
      console.warn("flipPrev() is not available on flipbookRef.current!");
    }
  };

  return (
    <div className="flipbook-container">
      <button onClick={prevPage}>⬅ Prev</button>

      {numPages > 0 && (
        <HTMLFlipBook
          ref={(el) => {
            flipbookRef.current = el;
            setIsFlipbookReady(!!el); // Ensure flipbook is ready
          }}
          width={600}
          height={800}
          showCover={true}
        >
          {[...Array(numPages).keys()].map((pageIndex) => (
            <div key={pageIndex} className="flip-page">
              <Document file={pdfUrl} onLoadSuccess={onDocumentLoadSuccess}>
                <Page pageNumber={pageIndex + 1} width={600} />
              </Document>
            </div>
          ))}
        </HTMLFlipBook>
      )}

      <button onClick={nextPage}>Next ➡</button>
    </div>
  );
};

export default Flipbook;
