import React, { useState, useEffect, useRef } from "react";
import { Document, Page, pdfjs } from "react-pdf";
import HTMLFlipBook from "react-pageflip";
import { useLocation } from "react-router-dom";
import "react-pdf/dist/esm/Page/AnnotationLayer.css";
import "react-pdf/dist/esm/Page/TextLayer.css";

// Set the worker source for react-pdf
pdfjs.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

const Flipbook = () => {
  const location = useLocation();
  const pdfUrl = location.state?.pdfUrl || "";
  const [numPages, setNumPages] = useState(null);
  const flipbookRef = useRef(null);
  const [flipbookReady, setFlipbookReady] = useState(false);

  useEffect(() => {
    console.log("PDF URL received:", pdfUrl);
  }, [pdfUrl]);

  const onDocumentLoadSuccess = ({ numPages }) => {
    console.log("PDF Loaded, Total Pages:", numPages);
    setNumPages(numPages);
  };

  useEffect(() => {
    // Check if flipbookRef is assigned
    if (flipbookRef.current) {
      console.log("‚úÖ Flipbook is ready!");
      setFlipbookReady(true);
    } else {
      console.warn("‚ö† Flipbook is still not ready!");
    }
  }, [numPages]);

  const nextPage = () => {
    console.log("‚è© Trying to go to next page...");
    if (!flipbookRef.current) {
      console.warn("‚ö† Flipbook is not ready yet!");
      return;
    }

    console.log("Flipbook instance:", flipbookRef.current);
    console.log("Available methods:", Object.keys(flipbookRef.current));

    if (typeof flipbookRef.current.pageFlip === "function") {
      flipbookRef.current.pageFlip().flipNext();
    } else {
      console.warn("‚ö† flipNext() is not available on flipbookRef.current!");
    }
  };

  const prevPage = () => {
    console.log("‚è™ Trying to go to previous page...");
    if (!flipbookRef.current) {
      console.warn("‚ö† Flipbook is not ready yet!");
      return;
    }

    if (typeof flipbookRef.current.pageFlip === "function") {
      flipbookRef.current.pageFlip().flipPrev();
    } else {
      console.warn("‚ö† flipPrev() is not available on flipbookRef.current!");
    }
  };

  return (
    <div className="flipbook-container">
      <button onClick={prevPage} disabled={!flipbookReady}>
        ‚¨Ö Prev
      </button>

      {numPages > 0 && (
        <HTMLFlipBook
          ref={flipbookRef}
          width={600}
          height={800}
          showCover={true}
          onInit={() => {
            console.log("üéâ Flipbook initialized!");
            setFlipbookReady(true);
          }}
        >
          {[...Array(numPages).keys()].map((pageIndex) => (
            <div key={pageIndex} className="flip-page">
              <Document file={pdfUrl} onLoadSuccess={onDocumentLoadSuccess}>
                <Page pageNumber={pageIndex + 1} width={600} />
              </Document>
            </div>
          ))}
        </HTMLFlipBook>
      )}

      <button onClick={nextPage} disabled={!flipbookReady}>
        Next ‚û°
      </button>
    </div>
  );
};

export default Flipbook;
