import React, { useEffect, useState, useRef } from "react";
import HTMLFlipBook from "react-pageflip";
import { Document, Page, pdfjs } from "react-pdf";
import { useLocation } from "react-router-dom";

import "react-pdf/dist/Page/AnnotationLayer.css";
import "react-pdf/dist/Page/TextLayer.css";
import arrowFwd from "../assets/arrow_forward.svg";
import arrowBack from "../assets/arrow_forward.svg";
pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.mjs`;

const Flipbook = () => {
  const [numPages, setNumPages] = useState(1);
  const [pageWidth, setPageWidth] = useState(700);
  const [pageHeight, setPageHeight] = useState(900);
  const flipbookRef = useRef(null);

  // Get PDF URL from query parameters
  const location = useLocation();
  const pdfUrl = new URLSearchParams(location.search).get("pdf");

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  const nextPage = () => flipbookRef.current?.pageFlip().flipNext();
  const prevPage = () => flipbookRef.current?.pageFlip().flipPrev();

  return (
    <div>

      {pdfUrl ? (
        <div className="flex gap-3 justify-center items-center bg-gray-900 p-24">
          <img onClick={prevPage} className="bg-black p-2 text-white rounded-3xl cursor-pointer" src={arrowBack} alt="prev" height={40} width={40} />

          <HTMLFlipBook width={pageWidth} height={pageHeight} ref={flipbookRef} showCover={true}>
            {[...Array(numPages).keys()].map((pNum) => (
              <div key={pNum} className="demoPage">
                <Document file={pdfUrl} onLoadSuccess={onDocumentLoadSuccess}>
                  <Page pageNumber={pNum + 1} width={pageWidth} height={pageHeight} scale={1} renderAnnotationLayer={false} renderTextLayer={false} />
                </Document>
              </div>
            ))}
          </HTMLFlipBook>

          <img onClick={nextPage} className="bg-black p-2 text-white rounded-3xl cursor-pointer" src={arrowFwd} alt="next" height={40} width={40} />
        </div>
      ) : (
        <div className="text-lg text-orange-200 m-auto bg-gray-900">No PDF selected.</div>
      )}

    </div>
  );
};

export default Flipbook;
