import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import logoImage1 from "../assets/cse.png"; // Update with actual path
import { Menu, X } from "lucide-react"; // Hamburger & close icons
import "./Navbar.css";
const Navbar = () => {
  const [isSticky, setIsSticky] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => setIsSticky(window.scrollY > 115);
    window.addEventListener("scroll", handleScroll);

    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setMenuOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      window.removeEventListener("scroll", handleScroll);
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <nav className={`Topnav ${isSticky ? "sticky" : ""}`} ref={dropdownRef}>
      <div className="logo">
        <a href="https://cses.iitism.ac.in/">
          <img src={logoImage1} alt="CSE Logo" />
        </a>
      </div>

      {/* Hamburger Button */}
      <button className="menu-toggle" onClick={() => setMenuOpen(!menuOpen)}>
        {menuOpen ? <X size={30} /> : <Menu size={30} />}
      </button>

      {/* Navbar Links */}
      <div className={`nav-group ${menuOpen ? "open" : "closed"}`}>
        <div className="nav-basic">
          <Link to="/" className="nav-link">Home</Link>
          <Link to="/teams" className="nav-link">Teams</Link>
        </div>
        <div className="nav-special">
          <Link to="/bytestreams" className="nav-link highlight bytestreams">Bytestreams</Link>
          <Link to="/buffered-readers" className="nav-link highlight buffered-readers active">
            Buffered Readers
            <div className="active-indicator"></div>
          </Link>
        </div>
        <div className="nav-about">
          <Link to="/about" className="nav-link">About Us</Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
