import React, { useState, useEffect } from "react";
import { Document, Page, pdfjs } from "react-pdf";
import { useLocation } from "react-router-dom";
import axios from "axios";
import './PDFFlipbook.css';
// Set up the PDF worker
pdfjs.GlobalWorkerOptions.workerSrc = `${process.env.PUBLIC_URL}/pdf.worker.mjs`;

const API_KEY = "AIzaSyCrKnWIvGZkh39oA58YfBR0EktQENVYDt0"; // Replace with your actual API key

const PdfViewer = () => {
  const location = useLocation();
  const fileId = location.state?.pdfUrl || ""; // Get the file ID from location state
  const [pdfBlobUrl, setPdfBlobUrl] = useState(null); // Blob URL for the PDF
  const [numPages, setNumPages] = useState(null); // Total number of pages
  const [pageNumber, setPageNumber] = useState(1); // Current page number
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    if (!fileId) {
      setError("No file ID provided.");
      return;
    }

    // Fetch the PDF file using the Google Drive API
    const fetchPdf = async () => {
      try {
        // Fetch the file content using the API key
        const response = await axios.get(
          `https://www.googleapis.com/drive/v3/files/${fileId}?alt=media&key=${API_KEY}`,
          { responseType: "arraybuffer" }
        );

        // Convert the file content to a Blob URL
        const blob = new Blob([response.data], { type: "application/pdf" });
        const blobUrl = URL.createObjectURL(blob);
        setPdfBlobUrl(blobUrl);
      } catch (err) {
        console.error("Error fetching PDF:", err);
        setError("Failed to fetch the PDF. Please check the file ID and try again.");
      }
    };

    fetchPdf();
  }, [fileId]);

  // Handle PDF load success
  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setPageNumber(1); // Reset to the first page when a new PDF is loaded
  };

  // Handle errors when loading the PDF
  const onDocumentLoadError = (error) => {
    console.error("Error loading PDF:", error);
    setError("Failed to load the PDF. Please check the URL and try again.");
  };

  return (
    <div className="pdf-viewer">
      {error ? (
        <p className="error-message">{error}</p>
      ) : (
        <>
          {/* Navigation Buttons */}
          <div className="navigation-buttons">
            <button
              onClick={() => setPageNumber((prev) => Math.max(prev - 1, 1))}
              disabled={pageNumber === 1}
            >
              ⬅ Previous
            </button>
            <button
              onClick={() => setPageNumber((prev) => Math.min(prev + 1, numPages))}
              disabled={pageNumber === numPages}
            >
              Next ➡
            </button>
          </div>

          {/* PDF Document */}
          {pdfBlobUrl && (
            <Document
              file={pdfBlobUrl}
              onLoadSuccess={onDocumentLoadSuccess}
              onLoadError={onDocumentLoadError}
            >
              <Page pageNumber={pageNumber} width={600} />
            </Document>
          )}

          {/* Page Indicator */}
          <div className="page-indicator">
            Page {pageNumber} of {numPages}
          </div>
        </>
      )}
    </div>
  );
};

export default PdfViewer;
