import React, { useState, useEffect } from "react";
import { Document, Page, pdfjs } from "react-pdf";
import { useLocation } from "react-router-dom";
import "react-pdf/dist/esm/Page/AnnotationLayer.css";
import "react-pdf/dist/esm/Page/TextLayer.css";

// Set the worker source for react-pdf
const workerSrc = `${process.env.PUBLIC_URL}/pdf.worker.mjs`;
pdfjs.GlobalWorkerOptions.workerSrc = workerSrc;

const PdfViewer = () => {
  const location = useLocation();
  const pdfUrl = location.state?.pdfUrl || "";
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Check if pdfUrl is valid
  useEffect(() => {
    console.log("PDF URL received:", pdfUrl);
    if (!pdfUrl) {
      setError("Error: No PDF URL provided.");
    }
  }, [pdfUrl]);

  // Verify if pdf.worker.mjs is accessible
  useEffect(() => {
    fetch(workerSrc)
      .then((response) => {
        if (!response.ok) {
          console.warn("⚠ pdf.worker.mjs is missing or inaccessible!");
        }
      })
      .catch(() => console.warn("⚠ Failed to fetch pdf.worker.mjs!"));
  }, []);

  const onDocumentLoadSuccess = ({ numPages }) => {
    console.log("PDF Loaded, Total Pages:", numPages);
    setNumPages(numPages);
    setLoading(false);
  };

  const onDocumentLoadError = (error) => {
    console.error("❌ PDF Load Error:", error);
    setError("Invalid or corrupted PDF file.");
    setLoading(false);
  };

  const nextPage = () => {
    if (pageNumber < numPages) {
      setPageNumber((prev) => prev + 1);
    }
  };

  const prevPage = () => {
    if (pageNumber > 1) {
      setPageNumber((prev) => prev - 1);
    }
  };

  return (
    <div className="pdf-viewer">
      {/* Display errors if any */}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Navigation buttons */}
      <button onClick={prevPage} disabled={pageNumber === 1 || error}>
        ⬅
      </button>

      {/* Display PDF Document */}
      <div className="pdf-container">
        {loading && <p>Loading PDF...</p>}
        <Document
          file={pdfUrl}
          onLoadSuccess={onDocumentLoadSuccess}
          onLoadError={onDocumentLoadError}
        >
          <Page pageNumber={pageNumber} width={600} />
        </Document>
      </div>

      <button onClick={nextPage} disabled={pageNumber === numPages || error}>
        ➡
      </button>

      {/* Page Counter */}
      {!error && <p>Page {pageNumber} of {numPages}</p>}
    </div>
  );
};

export default PdfViewer;
