import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './BufferedReadersPage.css';
import logoImage1 from '../assets/cse.png';
import bufferedReaderImage from '../assets/buffered_reader.png';

const BufferedReadersPage = () => {
  const [isSticky, setIsSticky] = useState(false);
  const [activeFilter, setActiveFilter] = useState('ALL');
  const [showDropdown, setShowDropdown] = useState(false);
  const [expandedCards, setExpandedCards] = useState({});
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.scrollY > 115);
    };
    
    window.addEventListener('scroll', handleScroll);
    
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };
    
    document.addEventListener('mousedown', handleClickOutside);
    
    return () => {
      window.removeEventListener('scroll', handleScroll);
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const toggleCardExpansion = (yearIndex, cardIndex) => {
    const cardKey = `${yearIndex}-${cardIndex}`;
    setExpandedCards(prev => ({
      ...prev,
      [cardKey]: !prev[cardKey]
    }));
  };

  // Add default description to all magazine items
  const defaultDescription = "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.";

  // Magazine data
  const magazines = {
    'ALL': [
      { year: '2018 - 19', items: [
        { season: 'MONSOON', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage, description: defaultDescription },
        { season: 'WINTER', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage, description: defaultDescription }
      ]},
      { year: '2019 - 20', items: [
        { season: 'MONSOON', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage, description: defaultDescription },
        { season: 'WINTER', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage, description: defaultDescription }
      ]}
    ],
    '2018-19': [
      { year: '2018 - 19', items: [
        { season: 'MONSOON', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage, description: defaultDescription },
        { season: 'WINTER', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage, description: defaultDescription }
      ]}
    ],
    '2019-20': [
      { year: '2019-20', items: [
        { season: 'MONSOON', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage, description: defaultDescription },
        { season: 'WINTER', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage, description: defaultDescription }
      ]}
    ]
  };

  const filterOptions = ['ALL', '2018-19', '2019-20'];

  return (
    <div className="buffered-readers-container">
      {/* Navbar */}
      <div className={`Topnav ${isSticky ? 'sticky' : ''}`}>
        <div className="logo">
          <img src={logoImage1} alt="CSE Logo" />
        </div>
        <div className="nav-group">
          <div className="nav-basic">
            <Link to="/" className="nav-link">Home</Link>
            <Link to="/teams" className="nav-link">Teams</Link>
          </div>
          <div className="nav-special">
            <Link to="/bytestreams" className="nav-link highlight bytestreams">Bytestreams</Link>
            <Link to="/buffered-readers" className="nav-link highlight buffered-readers active">
              Buffered Readers
              <div className="active-indicator"></div>
            </Link>
          </div>
          <div className="nav-about">
            <Link to="/about" className="nav-link">About Us</Link>
          </div>
        </div>
      </div>

      {/* Archive Header */}
      <div className="archive-header">
        <div className="archive-header-content">
          <h1>BUFFERED READERS ARCHIVE</h1>
          <p>Explore these magazines to witness the rich history of our society</p>
          
          {/* Dropdown filter */}
          <div className="filter-dropdown-container" ref={dropdownRef}>
            <button 
              className="filter-dropdown-btn"
              onClick={() => setShowDropdown(!showDropdown)}
            >
              {activeFilter}
            </button>
            
            {showDropdown && (
              <div className="filter-dropdown-menu">
                {filterOptions.map((option, index) => (
                  <div 
                    key={index}
                    className="filter-option"
                    onClick={() => {
                      setActiveFilter(option);
                      setShowDropdown(false);
                    }}
                  >
                    {option}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Magazines Section */}
      <div className="magazines-section">
        {magazines[activeFilter].map((yearGroup, yearIndex) => (
          <div key={yearIndex} className="year-section">
            <div className="year-title">{yearGroup.year}</div>
            <div className="magazines-row">
              {yearGroup.items.map((magazine, cardIndex) => {
                const cardKey = `${yearIndex}-${cardIndex}`;
                const isExpanded = expandedCards[cardKey];
                
                return (
                  <div key={cardIndex} className={`magazine-card ${isExpanded ? 'expanded' : ''}`}>
                    <div className="magazine-content">
                      <div className="magazine-text">
                        <h3>{magazine.season}</h3>
                        <p>{magazine.month}</p>
                        <p>{magazine.version}</p>
                        <button className="read-btn">READ</button>
                      </div>
                      <div className="magazine-image-container">
                        <img src={magazine.image} alt={`${magazine.season}`} className="magazine-image" />
                      </div>
                      <button 
                        className="expand-btn"
                        onClick={() => toggleCardExpansion(yearIndex, cardIndex)}
                      >
                        {isExpanded ? '❮' : '❯'}
                      </button>
                    </div>
                    {isExpanded && (
                      <div className="magazine-description">
                        <p>{magazine.description}</p>
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>
        ))}
      </div>

      {/* Footer */}
      <footer className="footer">
        <p>CSE Society: IIT ISM<br/> Dhanbad</p>
      </footer>
    </div>
  );
};

export default BufferedReadersPage;
