import React, { useState, useEffect, useRef } from "react";
import { useLocation } from "react-router-dom";
import { Document, Page, pdfjs } from "react-pdf";
import HTMLFlipBook from "react-pageflip";

pdfjs.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

function Flipbook() {
  const location = useLocation();
  const pdfUrl = decodeURIComponent(new URLSearchParams(location.search).get("pdf"));

  console.log("PDF URL received:", pdfUrl); // ✅ Check if the URL is being received correctly

  const [numPages, setNumPages] = useState(null);
  const flipbookRef = useRef(null);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  return (
    <div>
      <h1>Flipbook Viewer</h1>
      {pdfUrl ? (
        <HTMLFlipBook width={600} height={800} ref={flipbookRef}>
          {[...Array(numPages).keys()].map((pNum) => (
            <div key={pNum}>
              <Document file={pdfUrl} onLoadSuccess={onDocumentLoadSuccess}>
                <Page pageNumber={pNum + 1} />
              </Document>
            </div>
          ))}
        </HTMLFlipBook>
      ) : (
        <p>No PDF selected</p>
      )}
    </div>
  );
}

export default Flipbook;
