import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './BufferedReadersPage.css';
import logoImage1 from '../assets/cse.png';
import bufferedReaderImage from '../assets/buffered_reader.png';

const BufferedReadersPage = () => {
  const [isSticky, setIsSticky] = useState(false);
  const [activeFilter, setActiveFilter] = useState('ALL');

  useEffect(() => {
    const handleScroll = () => {
      // Make navbar sticky when scrolling down
      setIsSticky(window.scrollY > 115);
    };
    
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Magazine data
  const magazines = {
    'ALL': [
      { year: '2018 - 19', season: 'MONSOON', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage, description: 'Some good short description (if available)' },
      { year: '2018 - 19', season: 'WINTER', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage },
      { year: '2018 - 19', season: 'MONSOON', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage },
      { year: '2018 - 19', season: 'WINTER', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage },
      { year: '2018 - 19', season: 'MONSOON', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage },
      { year: '2018 - 19', season: 'WINTER', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage }
    ],
    '2018-19': [
      { year: '2018 - 19', season: 'MONSOON', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage },
      { year: '2018 - 19', season: 'WINTER', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage }
    ],
    '2019-20': [
      { year: '2019-20', season: 'MONSOON', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage },
      { year: '2019-20', season: 'WINTER', month: 'MARCH', version: 'v 5.1', image: bufferedReaderImage }
    ]
  };

  return (
    <div className="buffered-readers-container">
      {/* Navbar */}
      <div className={`Topnav ${isSticky ? 'sticky' : ''}`}>
        <div className="logo">
          <img src={logoImage1} alt="CSE Logo" />
        </div>
        <div className="nav-group">
          <div className="nav-basic">
            <Link to="/" className="nav-link">Home</Link>
            <Link to="/teams" className="nav-link">Teams</Link>
          </div>
          <div className="nav-special">
            <Link to="/bytestreams" className="nav-link highlight bytestreams">Bytestreams</Link>
            <Link to="/buffered-readers" className="nav-link highlight buffered-readers active">
              Buffered Readers
              <div className="active-indicator"></div>
            </Link>
          </div>
          <div className="nav-about">
            <Link to="/about" className="nav-link">About Us</Link>
          </div>
        </div>
      </div>

      {/* Archive Header */}
      <div className="archive-header">
        <div className="archive-header-content">
          <h1>BUFFERED READERS ARCHIVE</h1>
          <p>Explore these magazines to witness the rich history of our society</p>
          
          {/* Dropdown filter */}
          <div className="filter-dropdown">
            <select 
              value={activeFilter} 
              onChange={(e) => setActiveFilter(e.target.value)}
              className="filter-select"
            >
              <option value="ALL">ALL</option>
              <option value="2018-19">2018-19</option>
              <option value="2019-20">2019-20</option>
            </select>
          </div>
        </div>
      </div>

      {/* Magazines Section */}
      <div className="magazines-section">
        <div className="magazines-container">
          {magazines[activeFilter].map((magazine, index) => (
            <div key={index} className="year-section">
              <h2 className="year-title">{magazine.year}</h2>
              <div className="magazines-row">
                <div className="magazine-card">
                  <div className="magazine-content">
                    <div className="magazine-text">
                      <h3>{magazine.season}</h3>
                      <p>{magazine.month}</p>
                      <p>{magazine.version}</p>
                      <button className="read-btn">READ</button>
                    </div>
                    <div className="magazine-image-container">
                      <img src={magazine.image} alt={`${magazine.season} ${magazine.year}`} className="magazine-image" />
                    </div>
                  </div>
                  {magazine.description && (
                    <div className="magazine-description">
                      <p>{magazine.description}</p>
                      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla</p>
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="scroll-indicator">
          <p>//horizontal scroll rhega</p>
        </div>
      </div>

      {/* Footer */}
      <footer className="footer">
        <p>CSE Society: IIT ISM Dhanbad</p>
      </footer>
    </div>
  );
};

export default BufferedReadersPage;
